const phoneMenu=document.querySelector(".nav-links-login"),btn=document.querySelector(".ham-menu").addEventListener("click",(()=>{phoneMenu.classList.toggle("active")})),urlsRow=document.querySelector(".urls"),input=document.querySelector("input"),err=document.querySelector(".err"),inputBtn=document.querySelector(".text-btn").addEventListener("click",(()=>{const e=document.querySelector("input").value;""===e?(input.style.border="1px solid red",err.style.display="block"):(input.style.border="none",err.style.display="none");(async()=>{const t=await fetch(`https://api.shrtco.de/v2/shorten?url=${e}`),n=(await t.json()).result.short_link3;urlsRow.innerHTML+=` <div> <p> ${e} </p>   <p> ${n} </p> \n      \n      <button> Copy Link <button>\n      </div>  `})()}));